mapper.select:
SELECT C.CQC_CODE AS CoCODE, COUNT(*) AS FLAG
FROM CAREER_UP U, CQ_COMMENT C
WHERE U.CQC_CODE = C.CQC_CODE
  AND C.CQ_CODE = #{arg0}
  AND U.MEM_CODE = #{arg1}   
GROUP BY C.CQC_CODE

mapper.update:
UPDATE C_QNA
SET VIEWS = (SELECT NVL(VIEWS,0) +1 FROM C_QNA WHERE CQ_CODE = #{arg0})
WHERE CQ_CODE = #{arg0}

mapper.delete:
DELETE
FROM CAREER_UP
WHERE CQC_CODE = #{arg0}
AND   MEM_CODE = #{arg1}

mapper.select:
SELECT COUNT(*) AS COUNT
FROM CAREER_UP
WHERE CQC_CODE = #{arg0}

SELECT C.TC_CODE AS CoCODE, COUNT(*) AS FLAG
FROM TECH_UP U, TQ_COMMENT C
WHERE U.TC_CODE = C.TC_CODE
  AND U.MEM_CODE = #{arg0}
  AND C.TQ_CODE = #{arg1}
  GROUP BY C.TC_CODE

mapper.update:
UPDATE TECH_QNA
SET VIEWS = (SELECT NVL(VIEWS,0) +1 FROM TECH_QNA WHERE TQ_CODE = #{arg0})
WHERE TQ_CODE = #{arg0}

mapper.delete:
DELETE
FROM TECH_UP
WHERE TC_CODE = #{arg0}
AND   MEM_CODE = #{arg1}

mapper.select:
SELECT COUNT(*) AS COUNT
FROM TECH_UP
WHERE TC_CODE = #{arg0}

SELECT V_CODE, TITLE, CQ_CODE
FROM CVOTE
WHERE CQ_CODE = #{arg0}
